	public void setDimensions(float width, float height) {
		_width = (int)width;
		_height = (int)height;
		_pg1.setSize(_width, _height);
		_pg1.beginDraw();
		_pg1.background(_p.bgColor);
		_pg1.endDraw();
		_loadingWordle = true;
	}

	public void changeWordle(String s){
		_nextWordle = new WordCram(_p).fromTextString(s).withColorer(Colorers.twoHuesRandomSatsOnWhite(_p)).toCanvas(_pg1)
				.sizedByWeight(7, 100);
		_loadingWordle = true;
		Ani.to(this, 2f, "_gTint", 0, Ani.LINEAR, "onEnd:resetTint");
	}

	public void resetTint(){
		Ani.to(this, 2f, "_gTint", 255);
		_wordle = _nextWordle;
		_loadingWordle = false;
	}

	public void display(float x, float y) {		
		_pg1.beginDraw();
//		if(_loadingWordle){
//			_pg1.background(_p.bgColor);
//		}
		if(_wordle.hasMore()){
			_wordle.drawNext();
		}
		
		_pg1.endDraw();

		_p.tint(255, _gTint);
		_p.imageMode(_p.CENTER);
		_p.image(_pg1, x, y);
	}

	public void update(String s){
		changeWordle(s);
	}
